import logo from './logo.svg';
import './App.css';

const daneSklep =[{
  imgName: "img/img1.png",
  name: "Morela",
  opis: "Eko morela",
  stan: 2,
  cena: 2.80,
  naStanie: true,
},
{
  imgName: "img/img1.png",
  name: "Winogrona",
  opis: "Eko winogrona",
  stan: 10,
  cena: 7.80,
  naStanie: true,
}

]

function App() {
  return (
    <div className='goraLewa'>

      <h1>Internetowy sklep z eko-warzywami</h1>

    <div className='goraPrawa'>
      <ol>
        <li>Warzywa</li>
        <li>Owoce</li>
        <li>Soki</li>
      </ol>


    <div className='glowny'>
        <Box/>


    <div className='stopka'>


    </div></div></div></div>


  );
}

function Box(){
  return (

    <div className="Foto">
        <Foto/>

    <div className="Opis">
        <Opis/>
        <LangList/>

    </div></div>

  );
};

function Foto(){
  return(
    <img className="imgFoto" src='img/img1.png'></img>
  );
};

function Opis(){

  const dane = daneSklep;
  const allItems = dane.length;


  return(
    <div>
      <h5>Technik Programista</h5>
      <p>Programista komputerowy, programista, deweloper, potocznie koder – osoba pisząca programy komputerowe w języku programowania.
         Większość programistów zna co najmniej kilka języków programowania, lecz specjalizuje się tylko w jednym z nich.</p>
    </div>
  );
};

function LangList(){
  return(
    <div className="langList">

      <Lang lag="React" emoji="❤️" color="#0099FF"/>
      <Lang lag="C#" emoji="🦈" color="#009933"/>
      <Lang lag="Kotlin" emoji="📱" color="#660099"/>
      <Lang lag="JavaScript" emoji="📜" color="#FFCC33"/>

    </div>
  );
};

function Lang(props){
  return(
    <div className="lang" style={{backgroundColor:props.color}}>

      <span>{props.lag}</span>
      <span>{props.emoji}</span>

    </div>

  );
};

export default App;
